import React, { useContext, useState, useEffect } from "react";
import axios from "axios";

const ImageContext = React.createContext();

export const useImage = () => {
  return useContext(ImageContext);
};

export const ImageProvider = ({ children }) => {
  const [images, setImages] = useState([]);
  const [index, setIndex] = useState(-1);
  const [active, setActive] = useState({ cat: true, shark: true });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const getImages = async () => {
      var endpoint = getActive();
      if (endpoint) {
        const api = axios.create({
          baseURL:
            process.env.REACT_APP_BASE_URL ||
            `http://localhost:5000/api/images/${endpoint}`,
        });
        const { data } = await api.get(endpoint);
        if (images.length == 10) setIndex(index * 2);
        else setIndex(index / 2);
        setImages(data);
      } else {
        setIndex(-1);
        setImages([]);
      }

      if (endpoint) {
      } else {
      }
    };
    setLoading(true);
    getImages();
    setLoading(false);
  }, [active]);

  const updateImages = (type) => {
    switch (type) {
      case "Cats":
        return setActive((prev) => ({
          ...prev,
          cat: !active["cat"],
        }));
      case "Sharks":
        return setActive((prev) => ({
          ...prev,
          shark: !active["shark"],
        }));
      default:
        return null;
    }
  };

  const getActive = () => {
    switch (active) {
      case active["cat"] && active["shark"]:
        return "all";
      case active["cat"] && !active["shark"]:
        return "cat";
      case !active["cat"] && active["shark"]:
        return "shark";
      default:
        return null;
    }
  };

  const value = {
    images,
    active,
    loading,
    updateImages,
  };

  return (
    <ImageContext.Provider value={value}>{children}</ImageContext.Provider>
  );
};
