import React, { useContext, useState, useEffect } from "react";
import axios from "axios";

const ImageContext = React.createContext();

export const useImage = () => {
  return useContext(ImageContext);
};

export const ImageProvider = ({ children }) => {
  const [images, setImages] = useState([]);
  const [active, setActive] = useState({ cat: true, shark: true });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const getImages = async () => {
      const api = axios.create({
        baseURL:
          process.env.REACT_APP_BASE_URL || "http://localhost:5000/api/images",
      });
      var endpoint;
      if (active["cat"] && active["shark"]) endpoint = "/all";

      //   let response;
      //   const cat = active["cat"]
      //   const shark = active["shark"]
      //   if (active["cat"] && active["shark"]) {
      //     response["data"] = await api.get("/all");
      //   } else if (){

      //   }

      //   switch (active) {
      //     case active["cat"] && active["shark"]:
      //       console.log("hi");
      //       data = await api.get("/all");
      //       console.log(data);
      //       break;
      //     case active["cat"] && !active["shark"]:
      //       data = await api.get("/cat");
      //       break;
      //     case !active["cat"] && active["shark"]:
      //       data = await api.get("/shark");
      //       break;
      //     default:
      //       break;
      //   }
      //   setImages(data);
    };
    setLoading(true);
    getImages();
    setLoading(false);
  }, [active]);

  const updateImages = (type) => {
    switch (type) {
      case "cat":
        setActive((a) => !a["cat"]);
        break;
      case "shark":
        setActive((a) => !a["shark"]);
        break;
      default:
        break;
    }
  };

  const value = {
    images,
    loading,
    updateImages,
  };
  return (
    <ImageContext.Provider value={value}>{children}</ImageContext.Provider>
  );
};
