import React, { useContext, useState, useEffect } from "react";
import axios from "axios";

const ImageContext = React.createContext();

export const useImage = () => {
  return useContext(ImageContext);
};

export const ImageProvider = ({ children }) => {
  const [images, setImages] = useState([]);
  const [index, setIndex] = useState(-1);
  const [active, setActive] = useState({ cat: false, shark: false });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const getImages = async () => {
      var endpoint = getActive();
      if (endpoint) {
        const url =
          process.env.REACT_APP_BASE_URL ||
          `http://localhost:5000/api/images/${endpoint}`;
        const { data } = await axios.get(url);
        if (images.length == 10) setIndex(index * 2);
        else setIndex(index / 2);
        setImages(data);
        console.log(data);
      } else {
        setIndex(-1);
        setImages([]);
      }
    };
    setLoading(true);
    getImages();
    setLoading(false);
  }, [active]);

  const updateImages = (type) => {
    switch (type) {
      case "Cats":
        return setActive((prev) => ({
          ...prev,
          cat: !active["cat"],
        }));
      case "Sharks":
        return setActive((prev) => ({
          ...prev,
          shark: !active["shark"],
        }));
      default:
        return null;
    }
  };

  const getActive = () => {
    console.log(active);
    switch ((active["cat"], active["shark"])) {
      case (true, true):
        return "all";
      case (true, false):
        return "cat";
      case (false, true):
        return "shark";
      default:
        return null;
    }
  };

  const value = {
    images,
    loading,
    updateImages,
    getActive,
  };

  return (
    <ImageContext.Provider value={value}>{children}</ImageContext.Provider>
  );
};
