import React, { useContext, useState, useEffect } from "react";
import axios from "axios";

const ImageContext = React.createContext();

export const useImage = () => {
  return useContext(ImageContext);
};

export const ImageProvider = ({ children }) => {
  const [images, setImages] = useState([]);
  const [active, setActive] = useState({ cat: false, shark: false });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const getImages = async () => {
      const api = axios.create({
        baseURL:
          process.env.REACT_APP_BASE_URL || "http://localhost:5000/api/images",
      });
      let data = [];
      switch (active) {
        case active["cat"] && active["shark"]:
          data = await api.get("/all");
          console.log(data);
        case active["cat"] && !active["shark"]:
          data = await api.get("/cat");
        case !active["cat"] && active["shark"]:
          data = await api.get("/shark");
      }
      setImages(data);
    };
    setLoading(true);
    getImages();
    setLoading(false);
  }, [active]);

  const updateImages = (type) => {
    switch (type) {
      case "cat":
        setActive((a) => !a["cat"]);
      case "shark":
        setActive((a) => !a["shark"]);
    }
  };

  const value = {
    images,
    loading,
    updateImages,
  };
  return (
    <ImageContext.Provider value={value}>{children}</ImageContext.Provider>
  );
};
